mapper.select:
select

from tbl_order_info
where 1=1

  AND order_no = #{orderNo,jdbcType=VARCHAR}


  AND uid = #{uid,jdbcType=VARCHAR}


  AND product_no = #{productNo,jdbcType=VARCHAR}


  AND service_atom_count = #{serviceAtomCount,jdbcType=INTEGER}


  AND total_quantity = #{totalQuantity,jdbcType=INTEGER}


  AND total_amount = #{totalAmount,jdbcType=DECIMAL}


  AND status = #{status,jdbcType=INTEGER}


  AND pay_id = #{payId,jdbcType=VARCHAR}


  AND pay_return_id = #{payReturnId,jdbcType=VARCHAR}


  AND delivery_type = #{deliveryType,jdbcType=VARCHAR}


  AND delivery_time = #{deliveryTime,jdbcType=VARCHAR}


  AND sub_quantity = #{subQuantity,jdbcType=INTEGER}


  AND total_sub = #{totalSub,jdbcType=INTEGER}


  AND finish_sub = #{finishSub,jdbcType=INTEGER}


  AND next_sub = #{nextSub,jdbcType=VARCHAR}


  AND next_sub_time = #{nextSubTime,jdbcType=TIMESTAMP}


  AND address_id = #{addressId,jdbcType=INTEGER}


  AND user_number = #{userNumber,jdbcType=INTEGER}


  AND remark = #{remark,jdbcType=VARCHAR}


  AND error_code = #{errorCode,jdbcType=VARCHAR}


  AND error_msg = #{errorMsg,jdbcType=VARCHAR}


  AND create_time >= #{startDate,jdbcType=TIMESTAMP}


  AND create_time <= #{endDate,jdbcType=TIMESTAMP}



  limit #{offset,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select
  count(id)
from tbl_order_info
